#include <bits/stdc++.h>
#pragma GCC optimize(3,"Ofast")
#define int long long
using namespace std;
const int N = 1e5 + 10;
int t, n, q, temp;
vector <int> a;
set <int> S;
void F(vector <int> V) {
	int Max = LLONG_MIN, Min = LLONG_MAX, Mid, Sum = 0;
	for (int i : V) Sum += i;
	S.insert(Sum);
	if (V.size() == 1) return;
	for (int i : V) Max = max(Max, i);
	for (int i : V) Min = min(Min, i);
	if(Max == Min) return;
	Mid = (Min + Max) >> 1;
	vector <int> l, r;
	l.clear();
	r.clear();
	for (int i : V)
		if (i <= Mid)
			l.push_back(i);
		else
			r.push_back(i);
	F(l);
	F(r);
	return;
}
signed main() {
	cin >> t;
	while (t--) {
		a.clear();
		S.clear();
		cin >> n >> q;
		for (int i = 1; i <= n; i++) {
			cin >> temp;
			a.push_back(temp);
		}
		F(a);
		for (int i = 1; i <= q; i++) {
			cin >> temp;
			cout << (S.count(temp) ? "Yes\n" : "No\n");
		}
	}
}
