The first test case is explained in the problem statement.

In the second test case, it is impossible to color all the letters of the text in red.
```cpp
//CF1714D Color with Occurrences
#include <bits/stdc++.h>
using namespace std;
int t;
char s[120];
int len;
int n;
char a[12][12];
int ai[12];
int dp[120];
int cov[120];
int pos[120];
bool check(int x, int t) {
	if (ai[t] > x) return false;
	for (int i = x, j = ai[t]; j >= 1; --i, --j) {
		if (s[i] != a[t][j]) return false;
	}
	return true;
}
int main() {
	cin >> t;
	while (t--) {
		cin >> (s + 1);
		len = strlen(s + 1);
		cin >> n;
		for (int i = 1; i <= n; ++i) {
			cin >> (a[i] + 1);
			ai[i] = strlen(a[i] + 1);
		}
		dp[0] = 0;
		for (int i = 1; i <= len; ++i) {
			dp[i] = 1e9;
			cov[i] = pos[i] = 0;
		}
		for (int i = 1; i <= len; ++i) {
			for (int j = 1; j <= n; ++j) {
				if (check(i, j)) {
					for (int k = i - ai[j]; k <= i - 1; ++k) {
						if (dp[k] + 1 < dp[i]) {
							dp[i] = dp[k] + 1;
							cov[i] = j;
							pos[i] = k;
						}
					}
				}
			}
		}
		if (dp[len] >= 1e9)
			cout << "-1\n";
		else {
			cout << dp[len] << '\n';
			for (int i = len; i; i = pos[i]) {
				cout << cov[i] << ' ' << i - ai[cov[i]] + 1 << '\n';
			}
		}
	}
	return 0;
}
